mapper.update:
update TB_BIS_BATCH_EXP set BATCH_NO = #{batchNo,jdbcType=VARCHAR},
CHECK_STATUS = #{checkStatus,jdbcType=CHAR}, SUCCESS_NUM = #{successNum,jdbcType=DECIMAL}, FAIL_NUM = #{failNum,jdbcType=DECIMAL}, TOTAL_NUM = #{totalNum,jdbcType=DECIMAL}, TOTAL_AMT = #{totalAmt,jdbcType=DECIMAL}, HANDLE_STATUS = #{handleStatus,jdbcType=CHAR}, EXP_TIME = #{expTime,jdbcType=TIMESTAMP}, EXP_R = #{expR,jdbcType=VARCHAR}, EXP_R_NAME = #{expRName,jdbcType=VARCHAR}, CHECK_R = #{checkR,jdbcType=VARCHAR}, CHECK_R_NAME = #{checkRName,jdbcType=VARCHAR}, CHECK_TIME = #{checkTime,jdbcType=TIMESTAMP}, CHECK_REASON = #{checkReason,jdbcType=VARCHAR}, EXP_REASON = #{expReason,jdbcType=VARCHAR},
BUSI_TYPE = #{busiType,jdbcType=CHAR} where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select  from TB_BIS_BATCH_EXP


	 	and instr(BATCH_NO,#{batchNo,jdbcType=VARCHAR})>0



	 	and CHECK_STATUS = #{checkStatus,jdbcType=CHAR}


	 	and SUCCESS_NUM = #{successNum,jdbcType=DECIMAL}


	 	and FAIL_NUM = #{failNum,jdbcType=DECIMAL}


	 	and TOTAL_NUM = #{totalNum,jdbcType=DECIMAL}


	 	and TOTAL_AMT = #{totalAmt,jdbcType=DECIMAL}


	 	and HANDLE_STATUS = #{handleStatus,jdbcType=CHAR}


	 	and EXP_R = #{expR,jdbcType=VARCHAR}


	 	and EXP_R_NAME = #{expRName,jdbcType=VARCHAR}


	 	and CHECK_R = #{checkR,jdbcType=VARCHAR}


	 	and CHECK_R_NAME = #{checkRName,jdbcType=VARCHAR}


	 	and CHECK_TIME = #{checkTime,jdbcType=TIMESTAMP}


	 	and BUSI_TYPE = #{busiType,jdbcType=CHAR}


	 	and to_char(EXP_TIME,'yyyy-MM-dd') >= to_char(#{startExpTime,jdbcType=TIMESTAMP},'yyyy-MM-dd')


	 	and to_char(EXP_TIME,'yyyy-MM-dd') <= to_char(#{endExpTime,jdbcType=TIMESTAMP},'yyyy-MM-dd')


	 	and to_char(CHECK_TIME,'yyyy-MM-dd') >= to_char(#{startCheckTime,jdbcType=TIMESTAMP},'yyyy-MM-dd')


	 	and to_char(CHECK_TIME,'yyyy-MM-dd') <= to_char(#{endCheckTime,jdbcType=TIMESTAMP},'yyyy-MM-dd')


ORDER BY EXP_TIME DESC,ID DESC

select count(ID) from TB_BIS_BATCH_EXP
where EXP_FILE_NAME = #{expFileName,jdbcType=VARCHAR}
and BUSI_TYPE = #{busiType,jdbcType=CHAR}
