mapper.select:
select
from TB_CHL_ACCOUNT_CHANGE_NOTICE where 1=1
and DATE_FORMAT(CREATE_TIME,'%Y%m%d') >= DATE_FORMAT(#{startCreateTime},'%Y%m%d')
and DATE_FORMAT(CREATE_TIME,'%Y%m%d') <= DATE_FORMAT(#{endCreateTime},'%Y%m%d')
AND instr(PAY_ID, #{payId}) > 0
AND instr(PLAT_TRADE_NO, #{platTradeNo}) > 0
and ACC_CHANGE_TYPE = #{accChangeType,jdbcType=CHAR}
and DR_CR = #{drCr,jdbcType=CHAR}
and NOTIFY_STATUS = #{notifyStatus,jdbcType=CHAR}
ORDER BY CREATE_TIME desc
